 async (config?: {
  /**
   * If given, will only provide `ContextualPromptResult`s for this path
   */
  prompt_projectRelativePath?: string;
  /**
   * If given, will only return the db result if it is part of this array
   */
  promptSlugs?: string[];
}): Promise<ContextualPromptResult[] | null> => {
  const { prompt_projectRelativePath, promptSlugs } =
    destructureOptionalObject(config);
  const projectRoot = getProjectRoot();
  if (!projectRoot) return null;
  const prompt_absolutePath = prompt_projectRelativePath
    ? path.join(projectRoot, prompt_projectRelativePath)
    : undefined;
  const exists = prompt_absolutePath
    ? fs.existsSync(prompt_absolutePath)
    : undefined;
  const stats =
    exists && prompt_absolutePath
      ? await fs.stat(prompt_absolutePath)
      : undefined;
  const isFolder = stats ? stats.isDirectory() : false;
  const isFile = stats ? stats.isFile() : false;
  const pathParse = prompt_absolutePath
    ? path.parse(prompt_absolutePath)
    : undefined;
  if (prompt_absolutePath && !exists) {
    console.log("You gave a path that doesn't exist");
    return null;
  }

  const dbResults = (await db.get("ContextualPromptResult")).map(
    omitUndefinedValues
  );

  const onlyForPath = prompt_projectRelativePath
    ? dbResults?.filter(
        (x) => x.prompt_projectRelativePath === prompt_projectRelativePath
      )
    : dbResults;

  const onlyForPrompts = promptSlugs
    ? onlyForPath.filter((result) => {
        if (!result.contextualPromptSlug) return false;

        if (promptSlugs.includes(result.contextualPromptSlug)) return true;

        return false;
      })
    : onlyForPath;

  if (!prompt_projectRelativePath || !pathParse || !prompt_absolutePath) {
    // we don't need the .index one
    return onlyForPrompts;
  }
  const contextualPromptResultsJsonPath = isFile
    ? path.join(pathParse.dir, ".index", pathParse.name, "prompt-results.json")
    : isFolder
    ? path.join(prompt_absolutePath, ".index", "prompt-results.json")
    : undefined;
  if (
    !contextualPromptResultsJsonPath ||
    !fs.existsSync(contextualPromptResultsJsonPath)
  ) {
    return onlyForPrompts;
  }

  const indexResults = await readJsonFile<ContextualPromptResult[]>(
    contextualPromptResultsJsonPath
  );

  if (!indexResults) {
    console.log(
      "We tried to read the results but couldn't",
      contextualPromptResultsJsonPath
    );
    return onlyForPrompts;
  }

  const allResults: ContextualPromptResult[] = onlyForPrompts
    .concat(indexResults)
    .sort((a, b) => {
      if (a.isFake) return 1;
      if (b.isFake) return -1;
      return 0;
    })
    .sort((a, b) => {
      if (a.isFavorite) return -1;
      if (b.isFavorite) return 1;
      return 0;
    });
  return allResults;
}
