 async (): Promise<TsFunction[]> => {
  const tsFunctions = await db.get("TsFunction");

  getPublicBundleConfig();
  const toolFunctions = tsFunctions.filter((x) => {
    const fn = sdk[x.name as keyof typeof sdk] as ApiFunction | undefined;
    const isTool = fn && typeof fn === "function" && fn.domain;
    return isTool;
  });

  return toolFunctions;
}
