async (
  prompt: string,
  headless?: boolean
): Promise<ProcessPromptFunctionResult> => {
  const api = new ChatGPTAPI({ headless });
  // open chromium and wait until you've logged in
  await api.init({ auth: "blocking" });
  // send a message and wait for the response
  const response = await api.sendMessage(prompt);
  api.close();
  return {
    isSuccessful: true,
    message: "Done",
    result: { thread: "", text: response },
  };
}
