 async (debug?: boolean) => {
  await forAllFolders({
    type: "operations",
    basePath: getPathsWithOperations(),
    command: "[ -d \"src\" ] && find src -name '*.d.ts' -delete || echo 'hoi'",
    shell: true,
  });

  await forAllFolders({
    type: "operations",
    basePath: getPathsWithOperations(),
    command: "[ -d \"src\" ] && find src -name '*.js' -delete || echo 'hoi'",
    shell: true,
  });

  await forAllFolders({
    type: "operations",
    basePath: getPathsWithOperations(),
    command:
      "[ -d \"src\" ] && find src -name '*.d.ts.map' -delete || echo 'hoi'",
    shell: true,
  });
}
