 async (debug: boolean, force: boolean) => {
  forAllFolders({
    type: "operations",
    basePath: getPathsWithOperations(),
    callback: async (folderPath, index) => {
      log(`#${index}: Let's do ${folderPath}`, { type: "success" });
      log("----------", { type: "success" });

      await rebuildOperation({
        operationBasePath: folderPath,
        debug,
        noExit: true,
        force,
      });

      return;
    },
  });
}
