 async (config: {
  basePath: string;
  folderNames: string[];
  ignore?: string | string[];
  onlyRoot?: boolean;
}) => {
  const { basePath, folderNames, ignore, onlyRoot } = config;
  const removeFolderPromises = folderNames.map(async (folderName) => {
    const rmResult = await removeAll({
      basePath,
      type: "folder",
      folderName,
      shell: true,
      onlyRoot,
      ignore,
    });

    return;
  });

  await Promise.all(removeFolderPromises);

  return true;
}
