(
  apiUrl: string | undefined,
  apiFunctionName: string,
  query: { [name: string]: string[] | string | undefined }
): string | undefined => {
  if (!apiUrl) return;
  const queryString = Object.keys(query).length > 0 ? toQueryString(query) : "";
  const url = `${apiUrl}/function/${apiFunctionName}${queryString}`;
  return url;
}
