value:
(sizeBytes: number): string => {
  if (sizeBytes < 1024) return `${sizeBytes} bytes`;
  if (sizeBytes < 1024 * 1024) return `${Math.round(sizeBytes / 1024)} kb`;
  if (sizeBytes < 1024 * 1024 * 1024)
    return `${Math.round(sizeBytes / (1024 * 1024))} mb`;
  if (sizeBytes < 1024 * 1024 * 1024 * 1024)
    return `${Math.round(sizeBytes / (1024 * 1024 * 1024))} gb`;
  if (sizeBytes < 1024 * 1024 * 1024 * 1024 * 1024)
    return `${Math.round(sizeBytes / (1024 * 1024 * 1024 * 1024))} tb`;

  return "waaaay to big";
}
