(
  sourcePath: string,
  destinationPath: string
): Promise<string | undefined> => {
  return new Promise<string | undefined>((resolve, reject) => {
    ffmpeg({ source: sourcePath })
      .toFormat("mp3")
      .saveToFile(destinationPath)
      .on("end", () => {
        resolve(destinationPath);
      })
      .on("error", (e: Error) => {
        resolve(undefined);
      });
  });
}
