(
  serverContext: Context
): { isSuccessful: boolean; message: string } | void => {
  /**

  const authToken = (ctx as any).cookies?.authToken;
  const authToken2 = ctx.cookie?.authToken;

  console.log({
    authToken,
    authToken2,
    cookie: ctx.cookie,
    cookies: (ctx as any).cookies,
    headers: ctx.req.headers,
  });
  const isAuthed = functionAuthPlugin("uploadAsset", authToken);
  if (!isAuthed) {
    return {
      isSuccessful: false,
      isUnauthorized: true,
      message: "You can only do this when authenticated",
    };
  }
  */
  const isDownload = takeFirst(serverContext?.query.isDownload) === "1";

  const projectRelativeAssetPath = takeFirst(
    serverContext?.query.projectRelativeAssetPath
  );

  // Quick hack to keep it secure, only allow public stuff
  if (!projectRelativeAssetPath.startsWith("public/"))
    return {
      isSuccessful: false,
      message: "not allowed for now, only public data",
    };

  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return {
      isSuccessful: false,
      message: "No project root",
    };
  }

  if (!projectRelativeAssetPath) {
    console.log({ p: serverContext?.query });
    return {
      isSuccessful: false,
      message: "No path given",
    };
  }

  const absoluteAssetPath = path.join(projectRoot, projectRelativeAssetPath);

  const exists = fs.existsSync(absoluteAssetPath);

  if (!exists) {
    return {
      isSuccessful: false,
      message: "Invalid file",
    };
  }

  return serverDownloadReply(absoluteAssetPath, isDownload);
}
