async (
  functionContext: FunctionContext
): Promise<UploadAssetResult> => {
  const file = functionContext.serverContext?.files.file;
  if (!file) {
    console.log("Got no file, fuktup");
    return { isSuccessful: false, message: "No file found" };
  }

  const realFile = (Array.isArray(file)
    ? file[0]
    : file) as unknown as ReceivedFile;

  const functionFile = {
    size: realFile.size,
    path: realFile.path,
    name: realFile.name,
    type: realFile.type,
  };

  const randomName = generateRandomString(32);

  const temporaryAssetsFolderPath = getTemporaryAssetsFolderPath();

  const extension = getPreferredExtensionFromType(realFile.type);

  if (!extension) {
    return {
      isSuccessful: false,
      message: "couldn't figure out what you're trying to upload",
    };
  }

  const temporaryFileName = `${randomName}.${extension}`;
  const absoluteTemporaryDestinationPath = path.join(
    temporaryAssetsFolderPath,
    temporaryFileName
  );

  if (!fs.existsSync(temporaryAssetsFolderPath)) {
    await fs.mkdir(temporaryAssetsFolderPath, { recursive: true });
  }

  if (extension === "mp3") {
    // Conversion needed!
    const resultingPath = await convertToMp3(
      functionFile.path,
      absoluteTemporaryDestinationPath
    );
  } else {
    await fs.rename(functionFile.path, absoluteTemporaryDestinationPath);
  }

  return {
    isSuccessful: true,
    message: "File received",
    temporaryDestination: temporaryFileName,
  };
}
