export const itemGetBackendAssetUrl = (config: {
  item: AugmentedAnyModelType;
  backendAsset: BackendAsset;
  isDownload?: boolean;
}) => {
  const { backendAsset, item, isDownload } = config;

  if (!backendAsset.relativePath) {
    return undefined;
  }

  const url = getReferencedAssetApiUrl(
    apiUrl,
    item.projectRelativePath,
    backendAsset.relativePath,
    isDownload
  );
  return url;
};
