(props: { children: any }) => {
  const { children } = props;
  const [authToken, setAuthToken, { hydrated }] = useStore("api.authToken");

  // Ensure there is an authToken
  useEffect(() => {
    if (!hydrated) return;
    if (!authToken || authToken.length === 0) {
      setAuthToken(generateId());
    }
  }, [authToken, hydrated]);

  return children;
}
