value:
() => {
  const meQuery = queries.useGetMeWithContext();

  const person = meQuery.data?.result?.device?.currentPersonCalculated;
  const personProfileDetails: PersonProfileDetails | undefined = person
    ? {
        name: person.name,
        interestSlugs: person.interestSlugs,
        media: person.media,
        pictureImage: person.pictureImage,
        preferred_mediaChannelId: person.preferred_mediaChannelId,
      }
    : undefined;

  return (
    <Div className="flex flex-1 flex-row justify-center items-center">
      <Div className="w-96">
        <P className="text-4xl">Update your profile</P>

        {personProfileDetails ? (
          <PersonProfileDetailsForm
            personProfileDetails={personProfileDetails}
          />
        ) : null}
      </Div>
    </Div>
  );
}
