export const getDbModelsForBundle = async (
  bundleConfig: BundleConfig
): Promise<TsInterface[]> => {
  const bundleSummary = getBundleSummary(bundleConfig);

  const dbModels = getDbModelsFromOperations(
    bundleSummary.packageNames
      .concat(bundleSummary.moduleNames)
      .concat(bundleSummary.appNames)
  );
  return dbModels;
};
