 (bundleConfig: BundleConfig): BundleSummary => {
  const bundlesPath = getRootPath("bundles");
  const foldersFromRepo = bundlesPath
    ? bundleConfig.foldersFromRepo?.map((x) =>
        path.join(bundlesPath, kebabCase(bundleConfig.name), x)
      )
    : undefined;

  // const filesAndFoldersToInclude = [
  //   // ...(foldersFromRepo || []),
  //   bundleConfig.readmeRelativeFilePath,
  //   bundleConfig.docsRelativeFolderPath,
  // ].filter(notEmpty);

  const appNames = bundleConfig.bundles
    .map((x) => {
      const frontendOperationNames = makeArray(
        x.appShellOperationNames,
        x.serverOperationName
      );

      return frontendOperationNames;
    })
    .flat()
    .filter(onlyUnique2());

  const uiOperationNames = makeArray(
    bundleConfig.bundles.map((x) => x.uiOperationName).filter(notEmpty)
  );

  const bundlePublicOperationNames = bundleConfig.bundles
    .map(
      (x) =>
        x.operations
          ?.filter((x) => x.privacy === "public")
          .map((x) => x.operationName) || []
    )
    .flat();
  const bundlePrivateOperationNames = bundleConfig.bundles
    .map(
      (x) =>
        x.operations
          ?.filter((x) => x.privacy === "private")
          .map((x) => x.operationName) || []
    )
    .flat();

  const packageNames = bundleConfig.dependencies
    .filter((x) => x.privacy === "public")
    .map((x) => x.operationName)
    .concat(uiOperationNames)
    .concat(bundlePublicOperationNames)
    .filter(onlyUnique2());

  const moduleNames = bundleConfig.dependencies
    .filter((x) => x.privacy === "private")
    .map((x) => x.operationName)
    .concat(bundlePrivateOperationNames)
    .filter(onlyUnique2());

  const summary: BundleSummary = {
    // filesAndFoldersToInclude,
    packageNames,
    moduleNames,
    appNames,
  };

  return summary;
}
