 async (config?: {
  manualProjectRoot?: string;
}): Promise<undefined | MarkdownReaderPage[]> => {
  const projectRoot = config?.manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  // the docs folder with all its files
  const docsPath = path.join(projectRoot, "docs");

  const relativeDocsPages: MarkdownReaderPage[] = (
    await getPublicMarkdownFilePaths(docsPath)
  ).map((x) => {
    const filePath = makeRelative(x, projectRoot);
    const queryPath = removeExtensionsFromPath(filePath);
    return { queryPath, filePath, isMenuItem: true };
  });

  const hasDocs = !!relativeDocsPages.find(
    (x) => x.queryPath.toLowerCase() === "docs/readme"
  );
  if (!hasDocs) {
    const hasRootReadme = fs.existsSync(path.join(projectRoot, "README.md"));
    if (hasRootReadme) {
      relativeDocsPages.unshift({
        queryPath: "docs/readme",
        filePath: "README.md",
        isMenuItem: true,
      });
    }
  }

  // and all operations (readmes)
  const operationBasePaths = getPathsWithOperations({
    manualProjectRoot: projectRoot,
  });

  const operationPagesPerType: { [key: string]: MarkdownReaderPage[] } =
    mergeObjectsArray(
      await Promise.all(
        operationBasePaths.map(async (basePath) => {
          const projectRelativeBasePath = makeRelative(basePath, projectRoot);
          const projectRelativeOperationPaths = (
            await exploreOperationFolders({ basePath })
          ).map((absolutePath) => makeRelative(absolutePath, projectRoot));

          const pages: MarkdownReaderPage[] = projectRelativeOperationPaths.map(
            (relativePath) => {
              const operationName = getLastFolder(relativePath);
              return {
                queryPath: relativePath,
                // operation filePath is README.md
                filePath: path.join(relativePath, "README.md"),
                internalLinkWord: operationName,
                isMenuItem: true,
              };
            }
          );

          return { [projectRelativeBasePath]: pages };
        })
      )
    );
  // NB: we need to get the right order.
  const operationPages: MarkdownReaderPage[] = isSensibleProject(projectRoot)
    ? [
        ...operationPagesPerType.packages,
        ...operationPagesPerType.apps,
        ...operationPagesPerType.modules,
      ]
    : [
        ...operationPagesPerType["operations/tools"],
        ...operationPagesPerType["operations/bundles"],
      ];

  const tsVariables = await db.get("TsVariable", {
    manualProjectRoot: projectRoot,
  });

  const tsFunctions = await db.get("TsFunction", {
    manualProjectRoot: projectRoot,
  });
  const tsInterfaces = await db.get("TsInterface", {
    manualProjectRoot: projectRoot,
  });

  const typescriptPages: MarkdownReaderPage[] = [
    ...tsVariables,
    ...tsFunctions,
    ...tsInterfaces,
  ]
    .map((item) => {
      const operationPath = findOperationBasePath(
        path.join(projectRoot, item.projectRelativePath)
      );
      if (!operationPath) return;
      const projectRelativeOperationPath = makeRelative(
        operationPath,
        projectRoot
      );
      const page: MarkdownReaderPage = {
        internalLinkWord: item.name,
        queryPath: `${projectRelativeOperationPath}#${item.name}`,
        filePath: path.join(projectRelativeOperationPath, "README.md"),
        isMenuItem: false,
      };
      return page;
    })
    .filter(notEmpty);

  // const wordInclude: Include[] = [
  //   { referenceKey: "english_wordSlug" },
  //   { referenceKey: "root_wordSlug" },
  //   { referenceKey: "common_wordSlug" },
  //   { referenceKey: "definition_statementId" },
  // ];
  const words = await db.get("Word", {
    manualProjectRoot: projectRoot,
    // include: wordInclude,
  });
  const statements = await db.get("Statement", {
    manualProjectRoot: projectRoot,
    // include: { referenceKey: "wordSlugs", items: words, include: wordInclude },
  });

  const statementPages: MarkdownReaderPage[] = statements.map((item) => {
    const page: MarkdownReaderPage = {
      queryPath: `statement/${item.slug}`,
      statementId: item.id,
    };
    return page;
  });

  const wordPages: MarkdownReaderPage[] = words.map((item) => {
    const page: MarkdownReaderPage = {
      queryPath: `word/${item.slug}`,
      wordSlug: item.slug,
      internalLinkWord: item.name,
      isMenuItem: false,
    };

    return page;
  });

  /*

- variables, interfaces, functions lead to the operation readme with a hash
- words lead to /word/[slug] and have internalLinkWord [name]
- statements lead to /statement/[slug] but have no internalLinkWord. They are defined in `> markdown quote syntax or ==highlight syntax==` and should become clickable

*/
  const allPages: MarkdownReaderPage[] = [
    { queryPath: "", filePath: "README.md", isMenuItem: true },
    ...relativeDocsPages,
    ...operationPages,
    ...typescriptPages,
    ...statementPages,
    ...wordPages,
  ];

  return allPages;
}
