 async (
  queryPath: string,
  config?: { manualProjectRoot?: string }
): Promise<string | undefined> => {
  const projectRoot = config?.manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;
  const isSensible = isSensibleProject(projectRoot);

  if (!isSensible && queryPath === "") {
    const readmePath = await findFileNameCaseInsensitive(
      path.join(projectRoot, "text"),
      "README.md"
    );
    return readmePath;
  }

  if (queryPath === "") {
    const readmePath = await findFileNameCaseInsensitive(
      projectRoot,
      "README.md"
    );

    if (readmePath) return readmePath;

    const docsReadmePath = await findFileNameCaseInsensitive(
      path.join(projectRoot, "docs"),
      "README.md"
    );

    return docsReadmePath;
  }

  if (queryPath.startsWith("docs/")) {
    const realPath = path.join(projectRoot, `${queryPath}.md`);
    if (fs.existsSync(realPath)) return realPath;
    return;
  } else {
    // must be operation, use that convention

    const realPath = await findFileNameCaseInsensitive(
      path.join(projectRoot, queryPath),
      "README.md"
    );
    return realPath;
  }
}
