 async (
  page: Page
): Promise<"Login" | "Chat" | "Secure"> => {
  await page.goto("https://chat.openai.com/chat", {
    waitUntil: "domcontentloaded",
  });

  const sendMessageButton = await page.$(
    'button[class="absolute p-1 rounded-md text-gray-500 bottom-1.5 right-1 md:bottom-2.5 md:right-2 hover:bg-gray-100 dark:hover:text-gray-400 dark:hover:bg-gray-900 disabled:hover:bg-transparent dark:disabled:hover:bg-transparent"]'
  );

  const isTextAreaExist = await page.$("textarea");

  if (sendMessageButton && isTextAreaExist) {
    return "Chat";
  }

  const isSecurePageApear = await page.$x(
    "//h2[contains(., 'Checking if the site connection is secure')]"
  );

  const isLogin = 'button[class="btn flex justify-center gap-2 btn-primary"]';
  console.log({ isLogin });
  if (isSecurePageApear && isLogin) {
    console.log("Unexpected page appear please solve manually");
    const { captchas } = await isCaptchaExist(page);
    if (captchas.length > 0) {
      console.log("CAPTCHA DETECTED");
      const captchaSolveResponse = await solveReptcha(page);
      console.log({ captchaSolveResponse });
    }
    try {
      await page.waitForSelector(
        'button[class="btn flex justify-center gap-2 btn-primary"]',
        { timeout: 60000 }
      );
      console.log('"Login button appear');
      return "Login";
    } catch (e: Error | any) {
      console.error("Erro to to get login page so may be its a chat page open");
      return "Chat";
    }
  }
  return "Secure";
}
