 async (
  props: OpenAIChatProps
): Promise<OpenAIChatResponseType> => {
  const { prompt, thread, isHeadless } = props;
  console.log("openAIChat Called", props);

  // chat login test ...

  const { page, pageId } = await openPage(thread);
  if (!page) return { isSuccess: false, result: "Faild to create page." };
  if (thread !== pageId) {
    await page.goto("https://chat.openai.com/chat", {
      waitUntil: "domcontentloaded",
    });
  }

  let sendMessageButton = await page.$(
    'button[class="absolute p-1 rounded-md text-gray-500 bottom-1.5 right-1 md:bottom-2.5 md:right-2 hover:bg-gray-100 dark:hover:text-gray-400 dark:hover:bg-gray-900 disabled:hover:bg-transparent dark:disabled:hover:bg-transparent"]'
  );
  let isTextAreaExist = await page.$("textarea");

  if (!isTextAreaExist || !sendMessageButton) {
    const pageType = await detectChatGptPage(page);
    console.log({ pageType });
    if (pageType === "Login") {
      const authResponse = await chatGPTAuth(page);
      console.log({ authResponse });
      await new Promise((r) => setTimeout(r, 2000));
    }
  }

  // Wating and typing the prompt in the textarea field
  await page.waitForSelector("textarea", { timeout: 60000 });
  await page.waitForSelector(
    'button[class="absolute p-1 rounded-md text-gray-500 bottom-1.5 right-1 md:bottom-2.5 md:right-2 hover:bg-gray-100 dark:hover:text-gray-400 dark:hover:bg-gray-900 disabled:hover:bg-transparent dark:disabled:hover:bg-transparent"]',
    { timeout: 60000 }
  );

  sendMessageButton = await page.$(
    'button[class="absolute p-1 rounded-md text-gray-500 bottom-1.5 right-1 md:bottom-2.5 md:right-2 hover:bg-gray-100 dark:hover:text-gray-400 dark:hover:bg-gray-900 disabled:hover:bg-transparent dark:disabled:hover:bg-transparent"]'
  );
  isTextAreaExist = await page.$("textarea");

  console.log({ isTextAreaExist, sendMessageButton });
  if (isTextAreaExist && sendMessageButton) {
    console.log("inside of set value");
    await new Promise(async (resolve, reject) => {
      console.log("inside promise");
      resolve(
        await page.evaluate((evPrompt) => {
          const promtTextAreaField = document.getElementsByTagName("textarea");
          console.log({ promtTextAreaField });
          if (promtTextAreaField[0]) {
            //@ts-ignore
            promtTextAreaField[0].focus();
            promtTextAreaField[0].click();
            promtTextAreaField[0].value = evPrompt;
            const button = document.querySelector(
              'button[class="absolute p-1 rounded-md text-gray-500 bottom-1.5 right-1 md:bottom-2.5 md:right-2 hover:bg-gray-100 dark:hover:text-gray-400 dark:hover:bg-gray-900 disabled:hover:bg-transparent dark:disabled:hover:bg-transparent"]'
            );
            //@ts-ignore
            button?.click();
            return true;
          }
          console.log("TEXT AREA NOT FOUND");
        }, prompt)
      );
    });

    // Waiting for re-appear send message button that show current prompt is completed
    await page.waitForSelector(
      'button[class="absolute p-1 rounded-md text-gray-500 bottom-1.5 right-1 md:bottom-2.5 md:right-2 hover:bg-gray-100 dark:hover:text-gray-400 dark:hover:bg-gray-900 disabled:hover:bg-transparent dark:disabled:hover:bg-transparent"]',
      { timeout: 300000 }
    );

    console.log("ANSWER IS COMPLETED");
    // finding the last answer
    const result: string | undefined = await new Promise(
      async (resolve, reject) => {
        resolve(
          await page.evaluate(() => {
            // document.title = "bhagya 1234";
            const allAnswer = document.querySelectorAll(
              'div[class="w-full border-b border-black/10 dark:border-gray-900/50 text-gray-800 dark:text-gray-100 group bg-gray-50 dark:bg-[#444654]"]'
            );
            console.log({ allAnswer });
            if (allAnswer.length > 0) {
              const lastAnswer = allAnswer[allAnswer.length - 1]?.children;
              console.log({ lastAnswer });
            }

            return "";
          })
        );
      }
    );

    return {
      isSuccess: true,
      result,
      thread: pageId,
    };
  }
  return { isSuccess: false, result: "Text area not found", thread };
}
