(
  command: string,
  options: SpawnOptionsWithoutStdio
): Promise<{ success: boolean; response: string; data?: string[] }> => {
  return new Promise((resolve) => {
    const spawned = spawn(command, options);

    const data: string[] = [];
    spawned
      .on("exit", (code) => {
        resolve({ response: String(code), success: false, data });
      })
      .on("message", (message) => {
        //not sure if this does anything
      })
      .on("data", (message) => {
        //not sure if this does anything
        data.push(message);
      })
      .on("error", (err) => {
        resolve({ response: String(err), success: false, data });
      });
  });
}
