export const ClickableIcon = (button: ClickableIconType) => {
  return (
    <Button
      className="flex flex-col items-center"
      onClick={button.disabled ? undefined : button.onClick}
    >
      {button.icon ? (
        <Svg
          src={button.icon}
          width={24}
          height={24}
          className={button.disabled ? "text-gray-500" : "text-black"}
        />
      ) : button.emoji ? (
        <P
          className={`text-xl ${
            button.disabled ? "opacity-50" : "hover:scale-110"
          }`}
        >
          {button.emoji}
        </P>
      ) : null}
    </Button>
  );
};
