 async (config?: {
  manualProjectRoot?: string;
}): Promise<undefined | ReaderWebPage[]> => {
  const projectRoot = config?.manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  const codestoriesPaths = await getTodoPaths({
    categoryStack: ["codestories"],
    subExtension: "codestory",
    sort: "recent",
  });

  const pages: ReaderWebPage[] = await Promise.all(
    codestoriesPaths.map(async (projectRelativeFilePath) => {
      const absolutePath = path.join(projectRoot, projectRelativeFilePath);
      const markdownParse = fs.existsSync(absolutePath)
        ? await readMarkdownFile(absolutePath)
        : null;

      const readmeInfo = getMarkdownIntro(markdownParse);

      const readerWebPage: ReaderWebPage = {
        pageData: {
          projectRelativeFilePath,
          /**
          1) scrape images and the first alinea from the `projectRelativeFilePath`

          2) if there's an image in the frontmatter, use that. Otherwise, use the first image. Otherwise, no image.
          */
          imagePath: null,
          shortDescription: readmeInfo.firstParagraph,
        },
        queryPath: projectRelativeFilePath.replaceAll("/", "-"),
        isMenuHidden: false,
        menuTitle: readmeInfo.title || path.parse(projectRelativeFilePath).name,
      };
      return readerWebPage;
    })
  );

  const filteredPages = pages.filter(
    onlyUnique2<ReaderWebPage>((a, b) => a.queryPath === b.queryPath)
  );

  return filteredPages;
}
