 () => {
  const router = useRouter();
  const query = mapValuesSync(router.query, (value) => takeFirst(value));
  const model = useInfiniteGetDbModel();
  const allItems = model?.data?.pages
    ?.map((x) => x.result?.data)
    .flat()
    .filter(notEmpty) as AugmentedAnyModelType[];
  const instance = allItems?.find((x) => x.id === query.id);
  const modelName = useModelFromUrl();
  const metadataQuery = queries.useGetDbModelMetadata(modelName);
  const { tsInterface } = destructureOptionalObject(metadataQuery.data?.result);
  const simplifiedSchema = tsInterface?.type?.simplifiedSchema;
  const referencableModelNames = getReferencableModels(simplifiedSchema)
    ?.map((x) => x.interfaceName)
    .filter(notEmpty);

  return (
    <Div scroll className="py-4 px-8 lg:px-20">
      {model.isLoading ? (
        <FancyLoader />
      ) : !simplifiedSchema || !modelName ? (
        <Div>Something's wrong</Div>
      ) : (
        <UpsertForm
          modelName={modelName}
          simplifiedSchema={simplifiedSchema}
          instance={instance}
          referencableModelNames={referencableModelNames}
        />
      )}
    </Div>
  );
}
