 async (config?: {
  noOperationPath?: boolean;
  noOperationName?: boolean;
  noSrcRelativeFolder?: boolean;
  noPrefix?: boolean;
}) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return [];
  const { noOperationPath, noOperationName, noSrcRelativeFolder, noPrefix } =
    destructureOptionalObject(config);
  const dbModels = (await db.get("TsInterface")).filter((x) => x.isDbModel);

  const queryPaths = dbModels.map((dbModel) => {
    const projectRelativeOperationPath =
      operations[dbModel.operationName as keyof typeof operations];

    const slicedProjectRelativeOperationPath = projectRelativeOperationPath
      .split("/")
      // in bundles, we still want to make the distinction between modules and packages, so this is great.
      .slice(isBundle(projectRoot) ? 0 : 2)
      .join("/");
    const operationPart = noOperationName
      ? ""
      : noOperationPath
      ? dbModel.operationName + "/"
      : slicedProjectRelativeOperationPath + "/";

    const folder = getFolder(
      dbModel.operationRelativeTypescriptFilePath.slice("src/".length)
    );
    const folderPart = noSrcRelativeFolder
      ? ""
      : folder.length === 0
      ? ""
      : folder + "/";

    const prefixPart = noPrefix ? "" : "db/";
    const url = `${prefixPart}${operationPart}${folderPart}${dbModel.name}`;

    return url;
  });

  return queryPaths;
}
