(
  operationRelativeTypescriptFilePath: string
): string | undefined => {
  const srcPrefix = "src/";
  if (!operationRelativeTypescriptFilePath.startsWith(srcPrefix)) return;
  const withoutSrc = operationRelativeTypescriptFilePath.substring(
    srcPrefix.length
  );

  const parts = withoutSrc.split("/");

  parts.pop();

  const withoutFile = parts.join("/");

  if (withoutFile.length === 0) return;

  return withoutFile;
}
