async (
  projectRoot: string,
  todoPaths?: string[]
) => {
  const frontmatterPromises = todoPaths?.map(async (todoPath) => {
    const absoluteFilePath = path.join(projectRoot, todoPath);
    const frontmatter = (await readMarkdownFile(absoluteFilePath))?.parameters;
    return { [todoPath]: frontmatter };
  });
  const todoFrontmattersArray = frontmatterPromises
    ? await Promise.all(frontmatterPromises)
    : [];
  const todoFrontmattersMappedObject = mergeObjectsArray(todoFrontmattersArray);

  return todoFrontmattersMappedObject;
}
