 async (
  absoluteBasePath: string,
  sizeWidthPx?: number,
  quality?: number
) => {
  const results = (
    await explore({
      basePath: absoluteBasePath,
      extension: ["jpg", "jpeg", "png"],
    })
  ).map((x) => x.path);

  const compressionResult = await Promise.all(
    results.map((path) =>
      compressConvert(path, {
        targetFormat: "webp",
        quality: quality || 50,
        sizeWidthPx: sizeWidthPx || 1920,
      })
    )
  );

  const isSuccessful =
    compressionResult.filter(notEmpty).length === compressionResult.length;

  console.log("DONE", { isSuccessful });
}
