  const argumentsArray = process.argv.slice(2);

  if (argumentsArray.length > 0) {
    oneByOne(argumentsArray, async (absoluteOrRelativePath) => {
      const absoluteFilePath = absoluteOrRelativePath.startsWith(".")
        ? path.join(process.cwd(), absoluteOrRelativePath)
        : absoluteOrRelativePath;

      if (!absoluteFilePath || !fs.existsSync(absoluteFilePath)) {
        log("couldn't find that path");
        return;
      }

      // console.log({ operationNames, operationName });
      await compressMp4(absoluteFilePath);
    });
  }
}
