export const findMonorepoModules = async (operationName: string) => {
  const imports = await db.get("TsImport", { operationName });
  const monorepoModules = imports
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x.module : null
    )
    .filter(notEmpty)
    .filter(onlyUnique);

  return monorepoModules;
};

export const findDependenciesRecursively = async (
  operationName: string,
  /**
   * skip recursing on these ones because they are already found
   */
  already: string[],
  ignore?: string[]
): Promise<string[]> => {
  if (ignore?.includes(operationName)) return [];

  const notIgnored = (operationName: string) =>
    !ignore?.includes(operationName);
  const imports = await db.get("TsImport", { operationName });
  const monorepoModules = imports
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x.module : null
    )
    .filter(notEmpty)
    .filter(onlyUnique)
    .filter(notIgnored);

  const newAlready = already.concat(monorepoModules).filter(onlyUnique);

  const dependencyPromises = monorepoModules
    .filter((x) => !already?.includes(x))
    .map((operationName) =>
      findDependenciesRecursively(operationName, newAlready, ignore)
    );
  const dependencies = (await Promise.all(dependencyPromises)).flat();
  const all: string[] = monorepoModules.concat(dependencies);
  return all;
};

export const findAllDependencyOperations = async ({
  operationNames,
  ignoreOperationNames,
}: {
  operationNames: string[];
  ignoreOperationNames?: string[];
}) => {
  const dependencyPromises = operationNames.map((operationName) =>
    findDependenciesRecursively(
      operationName,
      operationNames,
      ignoreOperationNames
    )
  );
  const dependencies = (await Promise.all(dependencyPromises)).flat();
  return dependencies.filter(onlyUnique);
};

export const getDependencyObject = async () => {
  const allOperations = await exploreOperationFolders({});

  const dependencyObject = mergeObjectsArray(
    await Promise.all(
      allOperations.map(async (operationFolderPath) => {
        const operationName = getLastFolder(operationFolderPath);
        const monorepoModules = await findMonorepoModules(operationName);

        return { [operationName]: monorepoModules };
      })
    )
  );

  return dependencyObject;
};

