 async ({
  operationNames,
  ignoreOperationNames,
}: {
  operationNames: string[];
  ignoreOperationNames?: string[];
}) => {
  const dependencyPromises = operationNames.map((operationName) =>
    findDependenciesRecursively(
      operationName,
      operationNames,
      ignoreOperationNames
    )
  );
  const dependencies = (await Promise.all(dependencyPromises)).flat();
  return dependencies.filter(onlyUnique);
}
