 async (operationName: string) => {
  const imports = await db.get("TsImport", { operationName });
  const monorepoModules = imports
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x.module : null
    )
    .filter(notEmpty)
    .filter(onlyUnique);

  return monorepoModules;
}
