 async (
  allImports: TsImport[],
  operationName: string
) => {
  const imports = allImports.filter((x) => x.operationName === operationName);

  const monorepoModules = imports
    .map((x) =>
      x.isModuleFromMonorepo && x.isModuleResolved ? x.module : null
    )
    .filter(notEmpty)
    .filter(onlyUnique);

  return monorepoModules;
}
