value:
async ({
  imports,
  operations,
  operationNames,
  ignoreOperationNames,
  ignoreFilter,
}: {
  /**
   * All imports
   */
  imports: TsImport[];
  operations: Operation[];
  operationNames: string[];
  ignoreOperationNames?: string[];
  ignoreFilter?: (operationName: string) => boolean;
}) => {
  const dependencies = (
    await oneByOne(operationNames, async (operationName) => {
      process.stdout.write(`${operationName} ... `);

      return findDependenciesRecursively(
        imports,
        operations,
        operationName,
        operationNames,
        ignoreOperationNames,
        ignoreFilter
      );
    })
  ).flat();

  return dependencies.filter(onlyUnique);
}
