async () => {
  const allOperations = await exploreOperationFolders({});
  const allImports = await db.get("TsImport");

  const dependencyObject = mergeObjectsArray(
    await Promise.all(
      allOperations.map(async (operationFolderPath) => {
        const operationName = getLastFolder(operationFolderPath);
        const monorepoModules = await findMonorepoModules(
          allImports,
          operationName
        );

        return { [operationName]: monorepoModules };
      })
    )
  );

  return dependencyObject;
}
