async () => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;
  const publicBundleConfigPath = path.join(
    projectRoot,
    "public-bundle-config.json"
  );

  const publicBundleConfig = await readJsonFile<PublicBundleConfig>(
    publicBundleConfigPath
  );

  if (!publicBundleConfig) {
    return;
  }
  return publicBundleConfig;
}
