 async (
  functionName: keyof typeof sdk,
  parameters: undefined | any[],
  serverContext: Context
): Promise<RealApiReturnType<any>> => {
  const executionId = generateId();

  if (isGetEndpoint(functionName)) {
    // NB: shortcut for now, until we have cookie support:

    const result = await (sdk[functionName as keyof typeof sdk] as any)(
      serverContext
    );

    return result;
  }

  const performance: (PerformanceItem | undefined)[] = [];

  performance.push(getNewPerformance("start", executionId, true));

  // 1) upsert device
  const device = await upsertDevice(serverContext);
  if (!device) {
    return {
      isSuccessful: false,
      message: "Couldn't create device",
    };
  }

  performance.push(getNewPerformance("upsertDevice", executionId));

  // 2) get ts function
  const tsFunction = await getTsFunction(functionName);
  if (!tsFunction) {
    return {
      isSuccessful: false,
      message: `TsFunction file could not be found (${functionName}). Maybe you need to rebuild your operation and update your SDK?`,
    };
  }
  performance.push(getNewPerformance("getTsFunction", executionId));

  const { hasAuthorization, authorizations, groups } = getAuthorizationInfo(
    device,
    tsFunction
  );

  // // 3) auth

  //OLD way: privateAuthToken !== authToken
  // if (!hasAuthorization) {
  //   return {
  //     isSuccessful: false,
  //     isUnauthorized: true,
  //     message:
  //       "You are not authorized to execute this function, you might need to login.",
  //   };
  // }

  performance.push(getNewPerformance("auth", executionId));

  // 4) cache lookup
  const cacheLookupResult = cacheLookup(functionName, parameters);
  if (cacheLookupResult.hasValidCache) {
    return {
      result: cacheLookupResult.result,
      isSuccessful: true,
      isCached: true,
    };
  }
  performance.push(getNewPerformance("cacheLookup", executionId));

  // 5) validation
  const validationResult = validateInput(functionName, parameters, tsFunction);

  if (!validationResult.isValid) {
    return {
      isSuccessful: false,
      errors: validationResult.errors,
      message: `Your input has some errors. Input ${JSON.stringify(
        parameters
      )}.`,
    };
  }
  performance.push(getNewPerformance("inputValidation", executionId));

  // 4) running function
  const fn = sdk[functionName];
  if (typeof fn !== "function") {
    return {
      isSuccessful: false,
      message: `Function not found in the api-sdk: ${functionName}`,
    };
  }

  /**
   * Convention
   */

  const needsReturnRaw =
    functionName.endsWith(apiConventions.rawFunctionConventionSuffix) ||
    functionName.endsWith(apiConventions.getFunctionConventionSuffix);

  const needsFunctionContext =
    functionName.endsWith(apiConventions.contextFunctionConventionSuffix) ||
    needsReturnRaw;

  const functionContext: FunctionContext = {
    tsFunction,
    authorizations,
    device,
    groups,
    hasAuthorization,
    authToken: device.authToken,
    serverContext,
  };

  const parametersWithContext = needsFunctionContext
    ? [functionContext].concat(parameters || [])
    : parameters;

  // console.log(`We are authorized to execute ${functionName}! Let's do it`);
  // @ts-ignore
  const result = await fn(...parametersWithContext);
  performance.push(getNewPerformance("function", executionId));

  // 6) store performance
  storeFunctionExecution(
    tsFunction,
    parameters,
    result,
    performance.filter(notEmpty),
    false
  );

  // need to return this immediately without the surrounding object, because it might do stuff with that server context that needs to be returned
  if (needsReturnRaw) return result;

  if (result === undefined) {
    return {
      isSuccessful: true,
      isCached: false,
      message: "Function was executed but did not give any response",
      result: undefined,
    };
  }

  return {
    isSuccessful: true,
    isCached: false,
    result,
  };
}
