server.router.post(
  `/function/:functionName`,
  async (serverContext) => {
    const functionName = serverContext.params?.functionName;

    if (
      !functionName ||
      !functionApiKeys.includes(functionName) ||
      isGetEndpoint(functionName)
    ) {
      return {
        success: false,
        message: `Endpoint not found: POST /function/${functionName}`,
      };
    }

    const parameters: any[] | undefined = serverContext.data?.parameters;

    const result = await executeFunctionWithParameters(
      functionName as keyof typeof sdk,
      parameters,
      serverContext
    );

    return result;
  }
)
