value:
(
  device: Device,
  tsFunction: TsFunction
): AuthorizationInfo => {
  const everyPersonsGroups =
    device?.persons
      ?.map((x) => x.groups)
      .flat()
      .filter(notEmpty)
      .filter(onlyUnique2<Group>((a, b) => a.slug === b.slug)) || [];

  const everyPersonsAuthorizations =
    device?.persons?.map((x) => x.authorizations).flat() || [];

  const customGroupAuthorizations = everyPersonsGroups
    .map((group) => group.customAuthorizations)
    .flat();

  // Accumulating all authorizations: the public authorization of the function, the manual person authorizations, and the group authorizations
  const authorizations: Authorization[] = [
    ...everyPersonsAuthorizations,
    ...customGroupAuthorizations,
  ].filter(notEmpty);

  const hasAuthorization = !!authorizations.find((auth) => {
    const authAppliesToThisFunction =
      auth.tsFunctionId === tsFunction.id ||
      auth.authorizedOperationName === tsFunction?.operationName ||
      (auth.authorizedProjectRelativePath &&
        tsFunction.projectRelativePath.startsWith(
          auth.authorizedProjectRelativePath
        ));

    return authAppliesToThisFunction && auth.canExecute;
  });

  return { hasAuthorization, authorizations, groups: everyPersonsGroups };
}
