async (
  functionName: string
): Promise<TsFunction | null> => {
  const tsFunctionPath: string | undefined =
    sdkFunctionPaths[functionName as keyof typeof sdkFunctionPaths];
  const projectRoot = getProjectRoot();
  if (!tsFunctionPath || !projectRoot) {
    return null;
  }
  const absoluteTsFunctionPath = path.join(projectRoot, tsFunctionPath);
  const tsFunction = await readJsonFile<TsFunction>(absoluteTsFunctionPath);

  return tsFunction;
}
