async () => {
  if (functions.length) return functions;
  functions = await db.get("TsFunction");
  exportsArray = await db.get("TsExport");
  const fnIsExported = (fn: TsFunction) =>
    exportsArray.find(
      (e) => e?.operationName === fn?.operationName && e.name === fn.name
    );
  return functions.filter(fnIsExported);
}
