 async (config?: {
  manualProjectRoot?: string;
  skipYarnInstall?: boolean;
  dryrun?: boolean;
}) => {
  const manualProjectRoot = config?.manualProjectRoot;

  const { apiFunctions, jsFunctions, uiFunctions } =
    await getFunctionSdksContent({ manualProjectRoot });

  if (!apiFunctions || !jsFunctions || !uiFunctions) return;

  const promises = [
    // sdks
    newFunctionSdkOperation("sdk-api", apiFunctions, config),
    newFunctionSdkOperation("sdk-js", jsFunctions, config),
    newFunctionSdkOperation("sdk-ui", uiFunctions, config),

    /**
     * SDK Keys operations: Needed because "sdk-api" is node-only
     */
    newFunctionKeysSdkOperation(
      "sdk-api-keys",
      [
        {
          variableName: "functionApiKeys",
          values: apiFunctions.map((x) => x.name),
        },
      ],
      config
    ),
  ];

  await Promise.all(promises);
}
