async (
  bundleConfig: BundleConfig,
  config?: {
    manualProjectRoot?: string;
    skipYarnInstall?: boolean;
    dryrun?: boolean;
  }
) => {
  const promises = [
    newEnvSdk(bundleConfig, "public", config),
    newEnvSdk(bundleConfig, "private", config),
  ];

  const results = await Promise.all(promises);

  return results;
}
