async (config?: {
  manualProjectRoot?: string;
  skipYarnInstall?: boolean;
  dryrun?: boolean;
}) => {
  const skipYarnInstall = config?.skipYarnInstall;
  const dryrun = config?.dryrun;
  const manualProjectRoot = config?.manualProjectRoot;
  const projectRoot = manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  const tsFunctions = await db.get("TsFunction", { manualProjectRoot });

  const functionPathsObject = mergeObjectsArray(
    tsFunctions
      .map((tsFunction) => {
        const projectRelativePath = tsFunction.projectRelativePath;
        const exists = fs.existsSync(
          path.join(projectRoot, projectRelativePath)
        );
        if (!exists) return;
        return { [tsFunction.name]: projectRelativePath };
      })
      .filter(notEmpty)
  );

  const operationObjectString = `export const sdkFunctionPaths = ${JSON.stringify(
    functionPathsObject,
    null,
    2
  )};`;

  await newOperationWithFiles(
    "sdk-function-paths",
    await getSdkDescription("sdk-function-paths"),
    { "src/sdk-function-paths.ts": operationObjectString },
    { overwriteIfExists: true, skipYarnInstall, manualProjectRoot, dryrun }
  );
}
