async (config?: {
  manualProjectRoot?: string;
  skipYarnInstall?: boolean;
  dryrun?: boolean;
}) => {
  const manualProjectRoot = config?.manualProjectRoot;

  const functionsPerClassification = await getSdkFunctionsPerClassification({
    manualProjectRoot,
  });

  if (!functionsPerClassification) return;

  const jsFunctions = functionsPerClassification["cjs"].concat(
    functionsPerClassification["esm"]
  );

  const apiFunctions = functionsPerClassification["node-cjs"]
    .concat(functionsPerClassification["node-esm"])
    .concat(functionsPerClassification["cjs"])
    .concat(functionsPerClassification["esm"]);

  const uiFunctions = functionsPerClassification["ui-cjs"].concat(
    functionsPerClassification["ui-esm"]
  );

  const promises = [
    // sdks
    newFunctionSdkOperation("sdk-api", apiFunctions, config),
    newFunctionSdkOperation("sdk-js", jsFunctions, config),
    newFunctionSdkOperation("sdk-ui", uiFunctions, config),

    /**
     * SDK Keys operations: Needed because "sdk-api" is node-only
     */
    newFunctionKeysSdkOperation(
      "sdk-api-keys",
      [
        {
          variableName: "functionApiKeys",
          values: apiFunctions.map((x) => x.name),
        },
      ],
      config
    ),
  ];

  await Promise.all(promises);
}
