async (config?: {
  manualProjectRoot?: string;
  skipYarnInstall?: boolean;
  dryrun?: boolean;
}) => {
  const skipYarnInstall = config?.skipYarnInstall;
  const dryrun = config?.dryrun;
  const manualProjectRoot = config?.manualProjectRoot;
  const projectRoot = manualProjectRoot || getProjectRoot();
  if (!projectRoot) return;

  const tsInterfaces = await db.get("TsInterface", {
    manualProjectRoot,
    operationName: "*",
  });

  const interfacePathsObject = mergeObjectsArray(
    tsInterfaces
      .map((tsInterface) => {
        const projectRelativePath = tsInterface.projectRelativePath;
        const exists = fs.existsSync(
          path.join(projectRoot, projectRelativePath)
        );
        if (!exists) return;
        return { [tsInterface.name]: projectRelativePath };
      })
      .filter(notEmpty)
  );

  const operationObjectString = `export const sdkInterfacePaths = ${JSON.stringify(
    interfacePathsObject,
    null,
    2
  )};`;

  await newOperationWithFiles(
    "sdk-interface-paths",
    await getSdkDescription("sdk-interface-paths"),
    { "src/sdk-interface-paths.ts": operationObjectString },
    { overwriteIfExists: true, skipYarnInstall, manualProjectRoot, dryrun }
  );
}
