async (
  operationName: string
): Promise<string | undefined> => {
  const descriptionPath = path.join(
    __dirname,
    "..",
    "assets",
    "descriptions",
    `${operationName}.md`
  );

  if (!fs.existsSync(descriptionPath)) return;

  const contents = await fs.readFile(descriptionPath, "utf8");

  return contents;
}
