() => {
  forAllFolders({
    type: "operations",
    basePath: getPathsWithOperations(),
    callback: async (folderPath, index) => {
      const operationName = getLastFolder(folderPath);

      const classificationIndexed = getOperationClassification(folderPath);
      console.log(`${operationName}: ${classificationIndexed}`);

      await db.update(
        "Operation",
        (item) => item.name === operationName,
        (old) =>
          mergeNestedObject(old, { operation: { classificationIndexed } }),
        { operationName }
      );

      console.log("updated");

      return;
    },
  });
}
