value:
(
  parameters: Frontmatter | undefined,
  isDev: boolean
) => {
  // dev can always see it
  if (isDev) {
    return true;
  }

  // no parameters seems fine
  if (!parameters) {
    return true;
  }

  if (parameters.isDraft === "true" || parameters.isSecret === "true") {
    return false;
  }

  if (
    parameters.authorizedGroup?.trim() === "premium" ||
    parameters.authorizedGroup?.trim() === "enterprise"
  ) {
    // for now we have no way to check if you're premium/enterprise
    return false;
  }

  if (parameters.isPrivate === "true") {
    //
    return false;
  }

  return true;
}
