async (
  /**
   * If not given, will return all
   */
  contextType?: FileType,
  scopeProjectRelativePath?: string,
  isDev?: boolean
): Promise<ContextualPromptsObject> => {
  const contextualPromptsObject = await getContextualPromptsArray(
    scopeProjectRelativePath
  );

  const databaseContextualPromptSlugs =
    contextualPromptsObject.databaseResult.map((x) => x.slug);
  const contextualPrompts = Object.values(contextualPromptsObject).flat();

  const rightType = contextualPrompts
    .filter((x) => (contextType ? x.contextType?.includes(contextType) : true))
    .filter((x) => (isDev ? true : x.isFavorite))
    .map(omitUndefinedValues)
    // sort on fav first
    .sort((a, b) => {
      if (a.isFavorite && !b.isFavorite) return -1;
      if (b.isFavorite && !a.isFavorite) return 1;
      return 0;
    });

  const selectionContextualPrompts = rightType.filter(
    (x) => x.usesSelection || x.usesAnyContext
  );

  const pageContextualPrompts = rightType.filter(
    (x) => (!x.usesSelection && x.usesContext) || x.usesAnyContext
  );

  const folderContextualPrompts = rightType.filter(
    (x) =>
      (!x.usesSelection && !x.usesContext && x.folderContentContext) ||
      x.usesAnyContext
  );
  return {
    selectionContextualPrompts,
    pageContextualPrompts,
    folderContextualPrompts,
    databaseContextualPromptSlugs,
  };
}
